create table amazon_cat_templates
(
    id                serial
        primary key,
    created_at        timestamp default CURRENT_TIMESTAMP not null,
    category          varchar(100)                        not null,
    template_name     varchar(255)                        not null,
    fields            jsonb                               not null,
    field_definitions jsonb                               not null,
    valid_values      jsonb                               not null,
    variation_mapping jsonb,
    priority_themes   jsonb
);

comment on column amazon_cat_templates.variation_mapping is 'A JSONB object that maps internal standard variation attribute keys (e.g., color_name) to the actual column headers in the template file (e.g., Color). This is auto-generated by the TemplateManagementService.';

comment on column amazon_cat_templates.priority_themes is 'A JSONB array storing a prioritized list of variation themes for this category (e.g., ["COLOR/SIZE", "COLOR"]). Used to intelligently guide the LLM during variation correction.';

alter table amazon_cat_templates
    owner to mars_user;

create index idx_template_name
    on amazon_cat_templates (template_name);

